int countWordOccurrences(std::string filename, std::string word) {
    std::ifstream file(filename);
    std::string line;
    int count = 0;

    if (file.is_open()) {
        while (getline(file, line)) {
            size_t pos = 0;
            while ((pos = line.find(word, pos)) != std::string::npos) {
                count++;
                pos += word.length();
            }
        }
        file.close();
    } else {
        std::cerr << "Unable to open file: " << filename << std::endl;
    }

    return count;
}

int main() {
