const { Client, GatewayIntentBits, REST, Routes } = require('discord.js');
const { clientId, guildId, token } = require('./config.json');

const client = new Client({ intents: [GatewayIntentBits.Guilds] });

client.once('ready', () => {
    console.log('Ready!');
});

client.on('interactionCreate', async interaction => {
    if (!interaction.isChatInputCommand()) return;

    const { commandName } = interaction;

    if (commandName === 'ban') {
        if (!interaction.member.permissions.has('BanMembers')) {
            return interaction.reply({ content: 'You do not have permission to ban members!', ephemeral: true });
        }

        const user = interaction.options.getUser('user');
        const reason = interaction.options.getString('reason') || 'No reason provided';

        const member = interaction.guild.members.cache.get(user.id);
        if (!member) return interaction.reply({ content: 'User not found in this guild.', ephemeral: true });

        try {
            await member.ban({ reason });
            interaction.reply({ content: `Successfully banned ${user.tag} for reason: ${reason}` });
        } catch (error) {
            console.error(error);
            interaction.reply({ content: 'Failed to ban the user.', ephemeral: true });
        }
    }
});

const rest = new REST({ version: '10' }).setToken(token);

(async () => {
    try {
        console.log('Started refreshing application (/) commands.');

        await rest.put(
            Routes.applicationGuildCommands(clientId, guildId),
            { body: [{
                name: 'ban',
                description: 'Bans a user from the guild',
                options: [
                    {
                        type: 6, // USER type
                        name: 'user',
                        description: 'The user to ban',
                        required: true
                    },
                    {
                        type: 3, // STRING type
                        name: 'reason',
                        description: 'The reason for the ban',
                        required: false
                    }
                ]
            }] },
        );

        console.log('Successfully reloaded application (/) commands.');
    } catch (error) {
        console.error(error);
    }
})();

client.login(token);
```

