    if (num <= 1) return false; // 1 এবং এর চেয়ে কম সংখ্যা মৌলিক নয়
    if (num <= 3) return true; // 2 এবং 3 মৌলিক সংখ্যা

    if (num % 2 === 0 || num % 3 === 0) return false; // 2 এবং 3 দ্বারা বিভাজ্য সংখ্যা মৌলিক নয়

    for (let i = 5; i * i <= num; i += 6) {
        if (num % i === 0 || num % (i + 2) === 0) return false;
    }

    return true;
}

function printPrimeNumbers(numbers) {
    if (numbers.length !== 5) {
        console.log("অ্যারেতে ঠিক 5টি সংখ্যা থাকতে হবে।");
        return;
    }

    numbers.forEach(number => {
        if (isPrime(number)) {
            console.log(number);
        }
    });
}

