public class Solution {
    public boolean isPowerOfThree(int n) {
        // প্রথমে যাচাই করি যে n শূন্য বা নেতিবাচক নয়
        if (n <= 0) {
            return false;
        }

        // এখন n কে 3 দ্বারা বারবার ভাগ করি
        while (n % 3 == 0) {
            n /= 3;
        }

        // যদি শেষে n এর মান 1 হয়, তাহলে এটি 3^x এর শক্তি
        return n == 1;
    }
}
