```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int main() {
    std::string filename;
    std::string wordToFind;
    std::string line;
    std::string word;
    int count = 0;

    // Demander à l'utilisateur le nom du fichier et le mot à chercher
    std::cout << "Entrez le nom du fichier: ";
    std::getline(std::cin, filename);
    std::cout << "Entrez le mot à chercher: ";
    std::getline(std::cin, wordToFind);

    // Ouvrir le fichier
    std::ifstream file(filename);

    // Vérifier si le fichier est ouvert
    if (!file.is_open()) {
        std::cerr << "Erreur lors de l'ouverture du fichier!" << std::endl;
        return 1;
    }

    // Lire le fichier ligne par ligne
    while (std::getline(file, line)) {
        // Utiliser un flux de chaîne pour lire chaque mot
        std::istringstream iss(line);
        while (iss >> word) {
            // Comparer chaque mot avec le mot à chercher
            if (word == wordToFind) {
                count++;
            }
        }
    }

    // Fermer le fichier
    file.close();

    // Afficher le nombre d'occurrences
    std::cout << "Le mot '" << wordToFind << "' apparaît " << count << " fois dans le fichier." << std::endl;

    return 0;
}
```

