```cpp
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string filename;
    std::string wordToFind;
    std::string line;
    int count = 0;

    // Pata jina la faili na neno la kutafuta kutoka kwa mtumiaji
    std::cout << "Ingiza jina la faili: ";
    std::getline(std::cin, filename);
    std::cout << "Ingiza neno la kutafuta: ";
    std::getline(std::cin, wordToFind);

    // Fungua faili
    std::ifstream file(filename);

    // Hakikisha faili imefunguliwa kwa usahihi
    if (!file.is_open()) {
        std::cerr << "Haikuweza kufungua faili: " << filename << std::endl;
        return 1;
    }

    // Soma faili mstari kwa mstari
    while (std::getline(file, line)) {
        // Tafuta neno kwenye mstari
        std::size_t pos = line.find(wordToFind, 0);
        while (pos != std::string::npos) {
            count++;
            pos = line.find(wordToFind, pos + wordToFind.length());
        }
    }

    // Funga faili
    file.close();

    // Onyesha idadi ya matukio
    std::cout << "Neno '" << wordToFind << "' limepatikana mara " << count << " kwenye faili." << std::endl;

    return 0;
}
```

