```cpp
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string filename;
    std::string word;
    int count = 0;

    // Solicitar el nombre del archivo y la palabra a buscar
    std::cout << "Ingrese el nombre del archivo (con extensión): ";
    std::cin >> filename;
    std::cout << "Ingrese la palabra a buscar: ";
    std::cin >> word;

    // Abrir el archivo
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error al abrir el archivo: " << filename << std::endl;
        return 1;
    }

    std::string line;
    // Leer el archivo línea por línea
    while (std::getline(file, line)) {
        // Contar las apariciones de la palabra en la línea actual
        size_t pos = line.find(word);
        while (pos != std::string::npos) {
            count++;
            pos = line.find(word, pos + word.length());
        }
    }

    // Cerrar el archivo
    file.close();

    // Mostrar el resultado
    std::cout << "La palabra '" << word << "' aparece " << count << " veces en el archivo." << std::endl;

    return 0;
}
```

