    html = std::regex_replace(html, headerPattern, "<h1>$1</h1>");

    // Replace bold text
    std::regex boldPattern("\\*\\*(.*)\\*\\*");
    html = std::regex_replace(html, boldPattern, "<b>$1</b>");

    // Replace italic text
    std::regex italicPattern("\\*(.*)\\*");
    html = std::regex_replace(html, italicPattern, "<i>$1</i>");

    // Replace links
