    html = std::regex_replace(html, linkPattern, "<a href=\"$2\">$1</a>");

    // Replace images
    std::regex imagePattern("!\\[(.*)\\]\\((.*)\\)");
    html = std::regex_replace(html, imagePattern, "<img src=\"$2\" alt=\"$1\"/>");

