std::string markdownToHtml(const std::string& markdown) {
    std::string html = markdown;

    // Replace markdown formatting with html tags
    size_t pos = 0;
    while ((pos = html.find("**", pos)) != std::string::npos) {
        html.replace(pos, 2, "<strong>");
        pos = html.find("**", pos);
        html.replace(pos, 2, "</strong>");
    }

    pos = 0;
    while ((pos = html.find("__", pos)) != std::string::npos) {
        html.replace(pos, 2, "<strong>");
        pos = html.find("__", pos);
        html.replace(pos, 2, "</strong>");
    }

    pos = 0;
    while ((pos = html.find("*", pos)) != std::string::npos) {
        html.replace(pos, 1, "<em>");
        pos = html.find("*", pos);
        html.replace(pos, 1, "</em>");
    }

    pos = 0;
    while ((pos = html.find("_", pos)) != std::string::npos) {
        html.replace(pos, 1, "<em>");
        pos = html.find("_", pos);
        html.replace(pos, 1, "</em>");
    }

    return html;
}

int main() {
    std::string markdown = "This is **bold** and *italic* text.";
    std::string html = markdownToHtml(markdown);
    std::cout << html << std::endl;

    return 0;
}
