    html = std::regex_replace(html, italicRegex, "<i>$1</i>");

    // Replace links
    std::regex linkRegex("\\[(.*)\\]\\((.*)\\)");
    html = std::regex_replace(html, linkRegex, "<a href='$2'>$1</a>");

    // Replace images
    std::regex imageRegex("!\\[(.*)\\]\\((.*)\\)");
    html = std::regex_replace(html, imageRegex, "<img src='$2' alt='$1'>");

