```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int countWordInFile(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Не удалось открыть файл: " << filename << std::endl;
        return -1;
    }

    std::string line;
    int count = 0;
    while (getline(file, line)) {
        std::istringstream iss(line);
        std::string temp;
        while (iss >> temp) {
            if (temp == word) {
                count++;
            }
        }
    }

    file.close();
    return count;
}

int main() {
    std::string filename;
    std::string word;

    std::cout << "Введите имя файла: ";
    std::cin >> filename;
    std::cout << "Введите слово для подсчета: ";
    std::cin >> word;

    int result = countWordInFile(filename, word);
    if (result != -1) {
        std::cout << "Слово '" << word << "' встречается в файле " << filename << " " << result << " раз(а)." << std::endl;
    }

    return 0;
}
